import React, { Component } from 'react';
import {connect} from 'react-redux'
import { actions } from '../redux/modules/currencys'
import _ from 'lodash'
import 'bootstrap/dist/css/bootstrap.css'
import { currencySelector, isFetchingSelector } from '../redux/selectors/currencySelector'

class Currency extends Component {
    componentDidMount(){
        this.props.getCurrency()
        console.log(this.props);
    }

    render() {
        const {pupils, isFetching} = this.props

        return pupils && !isFetching ?
            <div className="col-xs-12 col-md-8 col-centered ">
                <div className ="table table-inverse row ">
                    <div className = "col">#</div>
                    <div className = "col-lg-2">Symbol</div>
                    <div className = "col">Name</div>
                    <div className = "col">Status</div>
                    <div className = "col">Description</div>
                    <div className = "col">Price</div>
                    <div className = "col">Subscribe</div>

                </div>
                <div className ="table table-condensed table-hover ">
                    {
                        _.map(pupils, root =>
                            <div className ="table table-striped table-hover row">
                                <div className = "col list-group-item"> {root.id} </div>
                                <div className = "col list-group-item"> {root.symbol} </div>
                                <div className = "col list-group-item"> {root.name} </div>
                                <div className = "col list-group-item"> {root.status} </div>
                                <div className = "col list-group-item"> {root.description} </div>
                                <div className = "col list-group-item"> {root.price} </div>

                                <button className="btn btn-primary btn btn-primary-outline" onClick="">Подписаться</button>
                            </div>
                        )
                    }

                </div>
            </div>
             :
            <div>Loading...</div>
    }
}

const mapDispatchToProps = {
    ...actions
}

const mapStateToProps = (state) => ({
    pupils:currencySelector(state),
    isFetching:isFetchingSelector(state)
})

export default connect(mapStateToProps, mapDispatchToProps)(Currency)
