import React, { Component } from 'react';
import {connect} from 'react-redux'
import { actions } from '../redux/modules/currencys'
import _ from 'lodash'

import { currencySelector, isFetchingSelector } from '../redux/selectors/currencySelector'

class Currency extends Component {
    componentDidMount(){
        this.props.getCurrency()
        console.log(this.props);
    }

    render() {
        const {pupils, isFetching} = this.props

        return pupils && !isFetching ?
            <div>"id" : 1 -"symbol" : BTC name : Bitcoin "status": 1 "description": hello1 "price" 3467.1
                "id" : 2 -"symbol" : BNT name : Bancor "status": 1 "description": hello "price" 0.495079
                "id" : 3 -"symbol" : XRP name : Ripple "status": 1 "description": hello "price" 0.294479
                "id" : 5 -"symbol" : LTC name : LiteCoin "status": 1 "description": hello "price" 31.1334
                "id" : 7 -"symbol" : ETH name : Ethereum "status": 1 "description": Ethereum "price" 106.403
                "id" : 10 -"symbol" : BCH name : Bitcoin Cash "status": 1 "description": Bitcoin Cash "price" 111.738
                "id" : 11 -"symbol" : SNC name : SunContract "status": 1 "description": SunContract "price" 0.0127854
                "id" : 12 -"symbol" : EOS name : EOS "status": 1 "description": EOS "price" 2.30856
                "id" : 13 -"symbol" : TRX name : Tron "status": 1 "description": Tron "price" 0.0277066
                "id" : 14 -"symbol" : ZEC name : ZCash "status": 1 "description": ZCash "price" 48.7962
                {
                    _.map(pupils, pupil=><div key={pupil.id}> "id" : {pupil.id} -"symbol" : {pupil.symbol} name : {pupil.name} "status": {pupil.status} "description": {pupil.description} "price" {pupil.price}</div>)
                }
            </div>
             :
            <div>Loading...</div>
    }
}

const mapDispatchToProps = {
    ...actions
}

const mapStateToProps = (state) => ({
    pupils:currencySelector(state),
    isFetching:isFetchingSelector(state)
})

export default connect(mapStateToProps, mapDispatchToProps)(Currency)
