import _ from 'lodash'
import { createAction , handleActions } from 'redux-actions'
import { resolve, request, reject } from 'redux-promised'


// ------------------------------------
// Constants
// ------------------------------------
export const NAMESPACE = 'students'
export const GET_VAL = NAMESPACE+'/'+'GET_VAL'
export const REVERSE_STUDENTS = NAMESPACE+'/'+'REVERSE_STUDENTS'
export const ADD_STUDENTS = NAMESPACE+'/'+'ADD_STUDENTS'
const url  = 'http://km.softbistro.online:20080/coins/'


// ------------------------------------
// Actions
// ------------------------------------
export const  getVal = () => {
    return{
        type: GET_VAL,
        meta: { promise: true },
        payload: fetch(url, {
            method: 'GET',
            mode:'cors'
        }).then(response=>response.json())
    }
}

export const reverseStudents  = createAction(REVERSE_STUDENTS)
export const addStudents  = createAction(ADD_STUDENTS)


export const actions = {
    getVal,
    reverseStudents,
    addStudents
}

// ------------------------------------
// Reducer
// ------------------------------------
export default handleActions({
    [resolve(GET_VAL)]: (state, { payload }) => {
        return{...state, data:payload, dataFethcing:false}
    },
    [reject(GET_VAL)]: (state, { payload }) => ({...state, data:null, dataFethcing:false}),
    [request(GET_VAL)]: (state, { payload }) => ({...state, data:null, dataFethcing:true}),
})

// ------------------------------------
// Helpers
// ------------------------------------
