import React, { Component } from 'react';
import {connect} from 'react-redux'
import { actions } from '../redux/modules/currencys'
import _ from 'lodash'
import 'bootstrap/dist/css/bootstrap.css'
import { currencySelector, isFetchingSelector } from '../redux/selectors/currencySelector'

class Currency extends Component {
    componentDidMount(){
        this.props.getCurrency()
        console.log(this.props);
    }

    render() {
        const {pupils, isFetching} = this.props

        return pupils && !isFetching ?
            <div>
                <table className="table">
                    <thead className="thead-dark">
                    <tr>
                        <th scope="col">id </th>
                        <th scope="col">First</th>
                        <th scope="col">Last</th>
                        <th scope="col">Handle</th>
                    </tr>
                    </thead>
                    </table>
                <ul className="list-group">
                {
                    _.map(pupils, pupil=> <li className="list-group-item list-group-item-success"><div key={pupil.id}> "id" : {pupil.id} "symbol" : {pupil.symbol} name : {pupil.name} "status": {pupil.status} "description": {pupil.description} "price" {pupil.price}</div></li>)
                }
                </ul>
            </div>
             :
            <div>Loading...</div>
    }
}

const mapDispatchToProps = {
    ...actions
}

const mapStateToProps = (state) => ({
    pupils:currencySelector(state),
    isFetching:isFetchingSelector(state)
})

export default connect(mapStateToProps, mapDispatchToProps)(Currency)
